mapper.select:

select

from cms_coupon_batch_item
where batch_no = #{batchNo,jdbcType=VARCHAR}

    and status = #{status,jdbcType=INTEGER}

select cbi.coupon_item_id as couponItemId, cbi.batch_no as batchNo, cbi.user_id as userId, cbi.status as status, cbi.create_time as createTime, cb.effective_start as effectiveStart, cb.effective_end as effectiveEnd, cb.activity_id as activityId, c.coupon_name as couponName, c.coupon_en_name as couponEnName, ct.type_name as couponType, c.coupon_rule as couponRule, c.coupon_remind as couponRemind, ca.activity_name as activityName, co.org_logo as orgLogo, co.org_name as orgName from cms_coupon_batch_item cbi left join cms_coupon_batch cb on cbi.batch_no = cb.batch_no left join cms_coupon c on cb.coupon_id = c.coupon_id left join cms_coupon_type ct on c.coupon_type = ct.type_id left join cms_activity ca on ca.activity_id = cb.activity_id left join sys_org co on co.org_id = c.org_id where cbi.user_id = #{userId,jdbcType=VARCHAR}
and status = #{status,jdbcType=INTEGER}
and cbi.is_valid = 1 and cb.is_valid = 1 and c.is_valid = 1 and ct.is_valid = 1

mapper.update:
update cms_coupon_batch_item
set user_id = #{userId},version = version+1
where coupon_item_id=#{couponItemId}
