mapper.select:
SELECT t_a.agreement_id AS agreementId, t_a.apply_id AS applyId, t_a.adopter_id AS adopterId, t_u1.nick_name AS adopterNickName, t_a.pet_id AS petId, t_p.pet_name AS petName, t_a.apply_by AS applyBy, t_u2.nick_name AS applyNickName, t_a.adopter_name AS adopterName, t_a.adopter_idcard AS adopterIdcard, t_a.adopter_address AS adopterAddress, t_a.adopter_phone AS adopterPhone, t_a.adopter_sign AS adopterSign, t_a.apply_name AS applyName, t_a.apply_idcard AS applyIdcard, t_a.apply_address AS applyAddress, t_a.apply_phone AS applyPhone, t_a.apply_sign AS applySign, t_a.sign_status AS signStatus, t_a.sign_time AS signTime, t_a.create_by AS createBy, t_a.create_date AS createDate FROM cms_adopt_agreement t_a LEFT JOIN cms_user t_u1 ON t_a.`adopter_id` = t_u1.`user_id` LEFT JOIN cms_user t_u2 ON t_a.`apply_by` = t_u2.`user_id` LEFT JOIN cms_adopt_pet t_p ON t_a.pet_id = t_p.pet_id WHERE t_a.`is_valid` = '1'
t_a.adopter_id = #{adopterId,jdbcType=VARCHAR}
t_a.apply_by = #{applyBy,jdbcType=VARCHAR}
t_a.pet_id = #{petId,jdbcType=VARCHAR}
t_a.sign_status = #{signStatus,jdbcType=VARCHAR}
