mapper.select:
SELECT apply_id AS applyId, ca.pet_id AS petId, apply_by AS applyBy, apply_time AS applyTime, ca.sex AS sex, ca.age AS age, ca.kept_pet AS keptPet, ca.marital_status AS maritalStatus, ca.housing_condition AS housingCondition, ca.job AS job, ca.address AS address, ca.mobile_phone AS mobilePhone, ca.to_adopter AS toAdopter, ca.apply_status AS applyStatus, ca.apply_resp as applyResp, DATE_FORMAT(ca.check_time,"%Y-%m-%d %T") as checkTime, ca.is_valid AS isValid, (SELECT media_path FROM cms_adopt_pet_media WHERE pet_id = ca.pet_id AND is_valid= 1 LIMIT 1) AS mediaPath, cp.pet_name as petName, cp.pet_age AS petAge, cp.pet_sex AS petSex, cu.nick_name AS nickName, cu.portrait AS portrait FROM cms_adopt_apply ca LEFT JOIN cms_adopt_pet cp ON ca.pet_id = cp.pet_id LEFT JOIN cms_user cu ON ca.apply_by = cu.user_id where ca.is_valid = '1'
AND cp.create_by=#{adopterId}
#{item,jdbcType=VARCHAR}
AND apply_by = #{applyBy,jdbcType=VARCHAR}
#{item,jdbcType=VARCHAR}

select count(1)
from cms_adopt_apply
where date_format(apply_time,'%y-%m-%d') = #{syncDate}
