mapper.select:
select group_id, group_type, group_name, group_info, group_banner, customer_support, sort,is_active, is_valid, create_date, (select count(1) from cms_adopt_group_user_rel where group_id = g.group_id and is_valid = 1) as fansCount, case group_type when '1' then (select count(1) from cms_adopt_group_user_rel where group_id = g.group_id and is_valid = 1) else (select count(1) from cms_adopt_group_activity where group_id = g.group_id and is_valid = 1) end as contentCount from cms_adopt_group g where is_valid = 1
and group_type = #{groupType}
and is_active = #{isActive}
order by sort , create_date desc
order by create_date desc

select
, (select count(1) from cms_adopt_group_user_rel where group_id = g.group_id and is_valid = 1) as fansCount, case group_type when '1' then (select count(1) from cms_adopt_group_user_rel where group_id = g.group_id and is_valid = 1) else (select count(1) from cms_adopt_group_activity where group_id = g.group_id and is_valid = 1) end as contentCount from cms_adopt_group g where is_valid = 1
and is_active = #{active}
and group_id =#{groupId}
